	}
	.clock-base {
		width: 250px;
		height: 250px;
		background-color: #eee;
		border-radius: 50%;
		box-shadow: 0 0 5px #eee;
	}
	.clock-indicator {
		z-index: 1;
		position: absolute;
		top: 15px;
		left: 15px;
		width: 230px;
		height: 230px;
	}
